import React, { useCallback, useEffect, useState, useRef } from "react";
import "./Slider.css";

const MultiRangeSlider = ({ min, max, onChange }) => {
  const [minVal, setMinVal] = useState(min);
  const [maxVal, setMaxVal] = useState(max);
  const minValRef = useRef(min);
  const maxValRef = useRef(max);
  const range = useRef(null);
  const left_value = useRef(null);
  const right_value = useRef(null);
  const circle_percentage = useRef(null);

  const getPercent = useCallback(
    (value) => Math.round(((value - min) / (max - min)) * 100),
    [min, max]
  );

  useEffect(() => {
    circle_percentage.current.style.left = `${Math.round((((maxVal-minVal)/100)*100)+1)}%`
  }, [minVal, maxVal]);

  useEffect(() => {
    const minPercent = getPercent(minVal);
    const maxPercent = getPercent(maxValRef.current);

    if (range.current) {
      range.current.style.left = `${minPercent}%`;
      range.current.style.width = `${maxPercent - minPercent}%`;
      left_value.current.style.left = `${minPercent-1}%`;
    }
  }, [minVal, getPercent]);

  useEffect(() => {
    const minPercent = getPercent(minValRef.current);
    const maxPercent = getPercent(maxVal);

    if (range.current) {
      range.current.style.width = `${maxPercent - minPercent}%`;
      right_value.current.style.left = `${maxPercent-1.5}%`;
    }
  }, [maxVal, getPercent]);

  useEffect(() => {
    onChange({ min: minVal, max: maxVal });
  }, [minVal, maxVal, onChange]);

  return (
    <div className="container">
      <input
        type="range"
        min={min}
        max={max}
        value={minVal}
        onChange={(event) => {
          const value = Math.min(Number(event.target.value), maxVal - 1);
          setMinVal(value);
          minValRef.current = value;
        }}
        className="thumb thumb--left"
      />

      <input
        type="range"
        min={min}
        max={max}
        value={maxVal}
        label={maxVal}
        onChange={(event) => {
          const value = Math.max(Number(event.target.value), minVal + 1);
          setMaxVal(value);
          maxValRef.current = value;
        }}
        className="thumb thumb--right"
      />

      <div className="slider">
        <div className="slider__track" />
        <div className="slider_circle" ref={circle_percentage}>
            <div className="slider_circle_inside">
                <span>{Math.round((((maxVal-minVal)/100)*100)+1)}%</span>
            </div>
        </div>
        <div ref={range} className="slider__range" />
        <div className="slider__left-value">{minVal}</div>
        <div className="slider__right-value">{maxVal}</div>
        <span className="left_value" ref={left_value}>{minVal}</span>
        <span className="right_value" ref={right_value}>{maxVal}</span>
      </div>
    </div>
  );
};

export default MultiRangeSlider;
